import React, { useState, useEffect, useRef } from "react";

import axios from "axios";
import "./ChatApp.css";

const ChatApp = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(() => {
    // Retrieve the saved theme from localStorage, default to light mode
    const savedMode = localStorage.getItem("darkMode");
    return savedMode === "true"; // Convert string to boolean
  });
  const stopTypingRef = useRef(false); // Use ref to handle immediate checks
  const typingTimeoutRef = useRef(null); // Store timeout reference

  const sendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage = { text: userInput, sender: "user" };
    setMessages([...messages, userMessage]);

    setIsTyping(true);
    stopTypingRef.current = false;

    try {
      const response = await axios.post("http://localhost:5000/chat", {
        question: userInput,
      });

      const botReply = response.data.reply;
      typeBotMessage(botReply);
    } catch (error) {
      console.error("Error connecting to the Flask backend:", error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: "Error communicating with the server.", sender: "bot" },
      ]);
      setIsTyping(false);
    }

    setUserInput("");
  };

  const typeBotMessage = (message) => {
    let currentText = "";
    const interval = 15;
    let index = 0;

    const typeCharacter = () => {
      if (stopTypingRef.current) {
        // Stop typing immediately
        clearTimeout(typingTimeoutRef.current);
        setIsTyping(false);
        return;
      }

      if (index < message.length) {
        currentText += message[index];
        setMessages((prevMessages) => [
          ...prevMessages.slice(0, -1),
          { text: currentText, sender: "bot" },
        ]);
        index++;
        typingTimeoutRef.current = setTimeout(typeCharacter, interval);
      } else {
        setIsTyping(false); // Finish typing
      }
    };

    typeCharacter();
  };

  const handleStopTyping = () => {
    stopTypingRef.current = true; // Set the ref to stop typing
    clearTimeout(typingTimeoutRef.current); // Clear the timeout immediately
    setIsTyping(false); // Update UI state
  };
  // eslint-disable-next-line no-unused-vars

  const toggleMode = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);

    // Add or remove the class from the body
    if (newMode) {
      document.body.classList.add("dark-mode");
      document.body.classList.remove("light-mode");
    } else {
      document.body.classList.add("light-mode");
      document.body.classList.remove("dark-mode");
    }

    // Save the theme preference to localStorage
    localStorage.setItem("darkMode", newMode);
  };

  useEffect(() => {
    // Apply the saved mode's class to the document body on load
    if (isDarkMode) {
      document.body.classList.add("dark-mode");
      document.body.classList.remove("light-mode");
    } else {
      document.body.classList.add("light-mode");
      document.body.classList.remove("dark-mode");
    }
  }, [isDarkMode]);



  return (
  <body className={`app ${isDarkMode ? "dark-mode" : "light-mode"}`}>
  <header className="app-header">
    <div className="headeritems">
    <button className="bt">
        <svg  class="icon-xl-heavy" width="24" height="24" viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg" className="icon-xl-heavy"><path d="M15.6729 3.91287C16.8918 2.69392 18.8682 2.69392 20.0871 3.91287C21.3061 5.13182 21.3061 7.10813 20.0871 8.32708L14.1499 14.2643C13.3849 15.0293 12.3925 15.5255 11.3215 15.6785L9.14142 15.9899C8.82983 16.0344 8.51546 15.9297 8.29289 15.7071C8.07033 15.4845 7.96554 15.1701 8.01005 14.8586L8.32149 12.6785C8.47449 11.6075 8.97072 10.615 9.7357 9.85006L15.6729 3.91287ZM18.6729 5.32708C18.235 4.88918 17.525 4.88918 17.0871 5.32708L11.1499 11.2643C10.6909 11.7233 10.3932 12.3187 10.3014 12.9613L10.1785 13.8215L11.0386 13.6986C11.6812 13.6068 12.2767 13.3091 12.7357 12.8501L18.6729 6.91287C19.1108 6.47497 19.1108 5.76499 18.6729 5.32708ZM11 3.99929C11.0004 4.55157 10.5531 4.99963 10.0008 5.00007C9.00227 5.00084 8.29769 5.00827 7.74651 5.06064C7.20685 5.11191 6.88488 5.20117 6.63803 5.32695C6.07354 5.61457 5.6146 6.07351 5.32698 6.63799C5.19279 6.90135 5.10062 7.24904 5.05118 7.8542C5.00078 8.47105 5 9.26336 5 10.4V13.6C5 14.7366 5.00078 15.5289 5.05118 16.1457C5.10062 16.
