      </button>
      <button className="bt2" onClick={toggleMode}>
  <div className="button-content">
  <span>7 JUNE</span>
  <img className="somber" src="dark.png"></img>

  </div>
</button>

      </div>
  </header>

    <div className="app-container">

      <div className="chat-container">
        <div id="chat-box">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={msg.sender === "user" ? "user-message" : "bot-message"}
            >
              {msg.text}
            </div>
          ))}
          {isTyping && <div className="typing-indicator">Typing...</div>}
        </div>
        <div className="x">
          <input
            type="text"
            id="user-input"
            placeholder="Ask something..."
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          {!isTyping ? (
            <img
              src="/send.png"
              alt="Send"
              className="send-button"
              onClick={sendMessage}
            />
          ) : (
            <img
              src="/stopsend.png"
              alt="Stop Typing"
              className="stop-button"
              onClick={handleStopTyping}
            />
          )}
        </div>
      </div>
    </div>
    <footer className="app-footer">
    <p>&copy; 2025 seven june. All rights reserved.</p>
    <p>
      Follow us on 
      <a href="https://www.youtube.com/@sevenjunebaby" target="_blank" rel="noopener noreferrer"> YouTube</a>, 
      <a href="https://github.com/sevenjunebaby" target="_blank" rel="noopener noreferrer">GitHub</a>, 
      and <a href="https://www.linkedin.com/in/wissal-yahia-16793a19b/" target="_blank" rel="noopener noreferrer">Linkedin</a>.
    </p>
  </footer>
    </body>
  );
};

export default ChatApp;
