import React, { useState } from "react";
import axios from "axios";
import "./ChatApp.css";

const ChatApp = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [stopTyping, setStopTyping] = useState(false);
  let typingTimeout;

  const sendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage = { text: userInput, sender: "user" };
    setMessages([...messages, userMessage]);

    setIsTyping(true);
    setStopTyping(false);

    try {
      const response = await axios.post("http://localhost:5000/chat", {
        question: userInput,
      });

      const botReply = response.data.reply;
      typeBotMessage(botReply);
    } catch (error) {
      console.error("Error connecting to the Flask backend:", error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: "Error communicating with the server.", sender: "bot" },
      ]);
      setIsTyping(false);
    }

    setUserInput("");
  };

  const typeBotMessage = (message) => {
    let currentText = "";
    const interval = 15;

    message.split("").forEach((char, index) => {
      setTimeout(() => {
        if (stopTyping) {
          clearTimeout(typingTimeout); 
          setIsTyping(false);
          return;
        }

        currentText += char;
        setMessages((prevMessages) => [
          ...prevMessages.slice(0, -1),
          { text: currentText, sender: "bot" },
        ]);

        if (index === message.length - 1) setIsTyping(false);
      }, index * interval);
    });
  };

  const handleStopTyping = () => {
    setStopTyping(true);
    clearTimeout(typingTimeout); // Signal to stop typing
  };

  return (
    <div className="app-container">
      <div className="video-background">
        <video autoPlay muted loop>
          <source src="/background.mp4" type="video/mp4" />
        </video>
      </div>

      <div className="chat-container">
        <div id="chat-box">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={msg.sender === "user" ? "user-message" : "bot-message"}
            >
              {msg.text}
            </div>
          ))}
          {isTyping && <div className="typing-indicator">Typing...</div>}
        </div>
        <div className="x">
          <input
            type="text"
            id="user-input"
            placeholder="Ask something..."
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          {!isTyping ? (
            <img
              src="/send.png"
              alt="Send"
              className="send-button"
              onClick={sendMessage}
            />
          ) : (
            <img
              src="/stopsend.png"
              alt="Stop Typing"
              className="stop-button"
              onClick={handleStopTyping}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatApp;
